import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Catalogue } from 'src/app/interface/catalogue';
import { Observable, Observer } from 'rxjs';
import { ProduitService } from 'src/app/service/produit.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-details-product',
  templateUrl: './details-product.component.html',
  styleUrls: ['./details-product.component.css']
})
export class DetailsProductComponent implements OnInit {

  constructor(private service : ProduitService, private route : Router, private activateRoute : ActivatedRoute) { }
  catalogue$? : Observable<Catalogue[]>;
  id$: number;
  product : Catalogue;
  sub : any;

  ngOnInit(): void {
    this.catalogue$ = this.service.getCatalogue();
    this.getParamsURL();    
  }

  detailsProduct(product : Catalogue){
    this.route.navigate(['/details', product.id]);
  }

  // get id from url
  getParamsURL() : number{
    this.sub = this.activateRoute.params.subscribe(params => {
      this.id$ = +params['id'];
    });
    return this.id$;
  }

}

<link href="//maxcdn.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css" rel="stylesheet" id="bootstrap-css">
<link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" />


<div class="container">
    <h3 class="h3">Fiche </h3>    
    <div class="row" >
        <div class="col-md-3 col-sm-6" *ngFor="let product of catalogue$ | async; index as i">
            <div class="product-grid2" *ngIf="id$ == product.id">
                <div class="product-image2" style = "height: 210px;">
                    <a href="#">
                        <img class="pic-1" src={{product.image}}>
                        <img class="pic-2" src={{product.image2}}>
                    </a>
                </div>
                <div class="product-content">
                    <h3 class="title">{{product.race}}</h3>
                    <span class="price">{{product.prix}} €</span>
                </div>
            </div>
        </div>
    </div>
</div>
