import { Component, OnInit } from '@angular/core';
import { Store } from '@ngxs/store';
import { Observable } from 'rxjs';
import { RemoveProduit } from  'src/app/shared/actions/cart-actions';
import { Produit } from 'src/app/models/produit.model';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {
  products$: Observable<Produit[]>;
  constructor(private store: Store) {
    this.products$ = this.store.select((state) => state.panier.produits);
  }

  ngOnInit(): void {
  }
  removeProduitFromPanier(productIndex: Number) {
    this.store.dispatch(new RemoveProduit(productIndex));
  }
}
<div class="container">
    <h2>Votre panier</h2>

    <div ngIf="(products$ | async)?.length == 0">
      <p>Le panier est vide</p>
    </div>

    <ul class="media-list">
      <li *ngFor="let product of products$ | async; index as i" class="media">
        <div class="panel panel-default">
          <div class="panel-body">
            <div class="media-left">
              <img class="media-object" src="{{ product.Image }}" alt="..." />
            </div>
            <div class="media-body">
              <h4 class="media-heading">{{ product.Intitule }}</h4>
              <p>{{ product.Description }}</p>
              <p>Prix : {{ product.Prix }} €</p>
              <button
                (click)="removeProduitFromPanier(i)"
                type="button"
                class="btn btn-danger"
              >
                <span
                  class="glyphicon glyphicon-remove"
                  aria-hidden="true"
                ></span>
                Supprimer du panier
              </button>
            </div>
          </div>
        </div>
      </li>
    </ul>
  </div>
