import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Catalogue } from 'src/app/interface/catalogue';
import { Observable, Observer } from 'rxjs';
import { ProduitService } from 'src/app/service/produit.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-details-product',
  templateUrl: './details-product.component.html',
  styleUrls: ['./details-product.component.css']
})
export class DetailsProductComponent implements OnInit {

  constructor(private service : ProduitService, private route : Router, private activateRoute : ActivatedRoute) { }
  catalogue$? : Observable<Catalogue[]>;
  id$?: number;
  product : Catalogue;
  sub : any;

  ngOnInit(): void {
    this.catalogue$ = this.service.getCatalogue();
    this.getParamsURL();    
  }

  detailsProduct(product : Catalogue){
    this.route.navigate(['/details', product.id]);
  }

  // get id from url
  getParamsURL() : number{
    this.sub = this.activateRoute.params.subscribe(params => {
      this.id$ = +params['id'];
    });
    return this.id$? this.id$ : 0;
  }

}
